function env_linux(){
    point=100
    
    if [[ $point -ge 90 ]]; then
        echo A;
    elif [[ $point -ge 80 ]]; then
        echo B;
    elif [[ $point -ge 70 ]]; then
        echo C;
    else 
        echo D;        
    fi;
}
